extension Salsa20Tests {
    static func allTests() -> [(String, (Salsa20Tests) -> () -> Void)] {
        let tests = [
            ("testSalsa20", testSalsa20),
            ("testCore", testCore),
            ("testVector1Py", testVector1Py),
            ("testSalsa20EncryptPartial", testSalsa20EncryptPartial),
        ]
        
        return tests
    }
